import React, { useState } from 'react';
import { Container, Nav, Navbar, NavDropdown } from 'react-bootstrap';
import { FaSun, FaMoon } from 'react-icons/fa';

const NavigationBar = ({ setLanguage, toggleDarkMode, darkMode }) => {
    const [selectedLanguage, setSelectedLanguage] = useState('id'); // Default: Indonesia

    const handleLanguageChange = (lang) => {
        setSelectedLanguage(lang);
        setLanguage(lang);
    };

    return (
        <Navbar className={darkMode ? "navbar-dark-mode" : "navbar-light-mode"} expand="lg" fixed="top">
            <Container>
                <Navbar.Brand href="/">Wisata Buton</Navbar.Brand>
                <Navbar.Toggle aria-controls="navbar-nav" />
                <Navbar.Collapse id="navbar-nav">
                    <Nav className="ms-auto">
                        <Nav.Link href="/">Home</Nav.Link>
                        <Nav.Link href="/destinasi">Destinasi</Nav.Link>

                        {/* Dropdown Bahasa */}
                        <NavDropdown title={selectedLanguage === "id" ? "Bahasa Indonesia" : "English"} id="language-dropdown">
                            <NavDropdown.Item onClick={() => handleLanguageChange('id')}>Indonesia</NavDropdown.Item>
                            <NavDropdown.Item onClick={() => handleLanguageChange('en')}>English</NavDropdown.Item>
                        </NavDropdown>

                        {/* Mode Gelap/Terang */}
                        <Nav.Link onClick={toggleDarkMode} style={{ cursor: "pointer" }}>
                            {darkMode ? <FaSun size={20} /> : <FaMoon size={20} />}
                        </Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default NavigationBar;
