import axios from "axios";

const API_URL = "http://localhost:5000/api/wisata"; // Sesuaikan dengan backend Anda

// Mengambil semua daftar wisata
export const getWisata = async () => {
    try {
        const response = await axios.get(API_URL);
        return response.data;
    } catch (error) {
        console.error("Error fetching wisata:", error);
        return [];
    }
};

// Mengambil detail wisata berdasarkan ID
export const getWisataById = async (id) => {
    try {
        const response = await axios.get(`${API_URL}/${id}`);
        console.log("API Response:", response.data); // Debugging
        return response.data;
    } catch (error) {
        console.error(`Error fetching wisata dengan id ${id}:`, error);
        return null;
    }
};
