import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../css/style.css";
import { 
    getWisata, tambahWisata, updateWisata, deleteWisata, 
    getLawa, tambahLawa, updateLawa, deleteLawa, 
    getBaluara, tambahBaluara, updateBaluara, deleteBaluara, 
    getSliders, tambahSlider, updateSlider, deleteSlider 
} from "../../api";

const AdminDashboard = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState("Manajemen Wisata");
    const [dataList, setDataList] = useState([]);
    const [formData, setFormData] = useState({ id: null, gambar: null });
    const [isEditMode, setIsEditMode] = useState(false);
    const [alertMessage, setAlertMessage] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 6;

    useEffect(() => {
        const adminToken = localStorage.getItem("adminToken");
        if (!adminToken) {
            navigate("/admin/login");
        }
    }, []);

    useEffect(() => {
        fetchData();
    }, [activeMenu, currentPage]);

    useEffect(() => {
        if (alertMessage) {
            const timer = setTimeout(() => setAlertMessage(null), 5000);
            return () => clearTimeout(timer);
        }
    }, [alertMessage]);

    const fetchData = async () => {
        let data = [];
        try {
            switch (activeMenu) {
                case "Manajemen Wisata":
                    data = await getWisata();
                    break;
                case "Manajemen Lawa":
                    data = await getLawa();
                    break;
                case "Manajemen Baluara":
                    data = await getBaluara();
                    break;
                case "Manajemen Slider":
                    data = await getSliders();
                    break;
                default:
                    data = [];
            }
            setDataList(data);
        } catch (error) {
            console.error("Gagal mengambil data:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
    };

    const handleFileChange = (e) => {
        setFormData({ ...formData, gambar: e.target.files[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditMode) {
                await updateSlider(formData.id, formData);
                setAlertMessage({ type: "success", text: "Gambar slider berhasil diperbarui!" });
            } else {
                await tambahSlider(formData);
                setAlertMessage({ type: "success", text: "Gambar slider berhasil ditambahkan!" });
            }
            fetchData();
            resetForm();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menyimpan gambar slider!" });
        }
    };

    const handleEdit = (data) => {
        setFormData(data);
        setIsEditMode(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Apakah Anda yakin ingin menghapus gambar ini?")) return;
        try {
            await deleteSlider(id);
            setAlertMessage({ type: "success", text: "Gambar slider berhasil dihapus!" });
            fetchData();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menghapus gambar slider!" });
        }
    };

    const resetForm = () => {
        setFormData({ id: null, gambar: null });
        setIsEditMode(false);
    };

    return (
        <div className="admin-layout">
            <div className="admin-sidebar">
                <h2>DASHBOARD ADMIN</h2>
                <ul>
                    <li onClick={() => setActiveMenu("Manajemen Wisata")} className={activeMenu === "Manajemen Wisata" ? "active" : ""}>Manajemen Wisata</li>
                    <li onClick={() => setActiveMenu("Manajemen Lawa")} className={activeMenu === "Manajemen Lawa" ? "active" : ""}>Manajemen Lawa</li>
                    <li onClick={() => setActiveMenu("Manajemen Baluara")} className={activeMenu === "Manajemen Baluara" ? "active" : ""}>Manajemen Baluara</li>
                    <li onClick={() => setActiveMenu("Manajemen Slider")} className={activeMenu === "Manajemen Slider" ? "active" : ""}>Manajemen Slider</li>
                </ul>
                <button className="btn-logout" onClick={handleLogout}>Logout</button>
            </div>

            <div className="admin-content">
                <h3>{activeMenu}</h3>

                <div className="admin-grid">
                    {activeMenu === "Manajemen Slider" && (
                        <div className="admin-form">
                            <h4>{isEditMode ? "Edit" : "Tambah"} Gambar Slider</h4>
                            <form className="form-admin" onSubmit={handleSubmit}>
                                <input type="file" accept="image/*" onChange={handleFileChange} required />
                                <button type="submit">{isEditMode ? "Update" : "Tambah"}</button>
                            </form>
                        </div>
                    )}

                    <div className="admin-table-container">
                        <table className="admin-table">
                            <thead>
                                <tr>
                                    <th>No</th>
                                    <th>Nama Gambar</th>
                                    <th>Gambar</th>
                                    <th>Aksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {dataList.map((item, index) => (
                                    <tr key={item.id}>
                                        <td>{index + 1}</td>
                                        <td>{item.gambar}</td>
                                        <td><img src={`/images/${item.gambar}`} alt="Gambar Slider" className="table-img" /></td>
                                        <td>
                                            <button onClick={() => handleEdit(item)}>Edit</button>
                                            <button onClick={() => handleDelete(item.id)}>Hapus</button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
