import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { getBaluaraById } from "../api";
import { Container, Button } from "react-bootstrap";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const DetailBaluara = ({ language, darkMode }) => {
    const { id } = useParams();
    const [baluara, setBaluara] = useState(null);
    const mapRef = useRef(null);

    useEffect(() => {
        getBaluaraById(id).then(data => setBaluara(data));
    }, [id]);

    useEffect(() => {
        if (baluara && baluara.latitude && baluara.longitude) {
            // Pastikan container map sudah ada
            const mapContainer = document.getElementById("map");
            if (!mapContainer) return;

            // Hapus map lama jika ada
            if (mapRef.current) {
                mapRef.current.remove();
                mapRef.current = null;
            }

            // Inisialisasi peta baru
            mapRef.current = L.map(mapContainer).setView([baluara.latitude, baluara.longitude], 16);
            L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png").addTo(mapRef.current);
            L.marker([baluara.latitude, baluara.longitude])
                .addTo(mapRef.current)
                .bindPopup(`<b>${baluara.nama}</b>`)
                .openPopup();
        }

        return () => {
            if (mapRef.current) {
                mapRef.current.remove();
                mapRef.current = null;
            }
        };
    }, [baluara]);

    if (!baluara) return <p>{language === "id" ? "Memuat..." : "Loading..."}</p>;

    return (
        <Container className={`mt-5 pt-5 ${darkMode ? "dark-mode" : ""}`}>
            <h2 className="mb-4">{baluara.nama}</h2>
            <img src={`/images/${baluara.gambar}`} alt={baluara.nama} className="img-fluid rounded mb-4" />

            <div className="row">
                {/* Peta di sebelah kiri */}
                <div className="col-md-4">
                    <div id="map" style={{ height: "400px", width: "100%", border: "1px solid #ccc" }}></div>
                </div>

                {/* Deskripsi di sebelah kanan */}
                <div className="col-md-8">
                    <p style={{ textAlign: 'justify' }}>
                        {language === "id" ? baluara.deskripsi_id : baluara.deskripsi_en}
                    </p>
                    <Button
                        variant="primary"
                        onClick={() => window.open(`https://www.google.com/maps/dir/?api=1&destination=${baluara.latitude},${baluara.longitude}`, "_blank")}
                    >
                        {language === "id" ? "Pergi Sekarang" : "Go Now"}
                    </Button>
                </div>
            </div>
        </Container>
    );
};

export default DetailBaluara;
