import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import NavigationBar from "./components/NavigationBar";
import Home from "./pages/Home";
import Detail from "./pages/Detail";
import Kontak from "./pages/Kontak";

const App = () => {
  const [language, setLanguage] = useState("id"); // Default: Bahasa Indonesia
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("darkMode") === "true"; // Simpan preferensi
  });

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode); // Simpan preferensi ke localStorage
  }, [darkMode]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <Router>
      <NavigationBar
        language={language}
        setLanguage={setLanguage}
        toggleDarkMode={toggleDarkMode}
        darkMode={darkMode}
      />
      <Routes>
        <Route path="/" element={<Home language={language} darkMode={darkMode} />} />
        <Route path="/detail/:id" element={<Detail language={language} darkMode={darkMode} />} />
      </Routes>
    </Router>
  );
};

export default App;
