import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import "../../css/style.css";
import { getWisata, tambahWisata, updateWisata, deleteWisata } from "../../api";
import { getLawa, tambahLawa, updateLawa, deleteLawa } from "../../api";
import { getBaluara, tambahBaluara, updateBaluara, deleteBaluara } from "../../api";

const AdminDashboard = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState("Manajemen Wisata");
    const [dataList, setDataList] = useState([]); // ✅ Sekarang akan digunakan
    const [formData, setFormData] = useState({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
    const [isEditMode, setIsEditMode] = useState(false);
    const [alertMessage, setAlertMessage] = useState(null);

    // ✅ Perbaikan: Gunakan useCallback untuk fetchData agar tidak menyebabkan peringatan missing dependency
    const fetchData = useCallback(async () => {
        let data;
        switch (activeMenu) {
            case "Manajemen Wisata":
                data = await getWisata();
                break;
            case "Manajemen Lawa":
                data = await getLawa();
                break;
            case "Manajemen Baluara":
                data = await getBaluara();
                break;
            default:
                data = [];
                break; // ✅ Tambahkan default case untuk menghilangkan warning
        }
        setDataList(data);
    }, [activeMenu]);

    // ✅ Perbaikan: Gunakan useEffect dengan fetchData sebagai dependency
    useEffect(() => {
        fetchData();
    }, [fetchData]);

    // ✅ Fungsi Logout
    const handleLogout = () => {
        localStorage.removeItem("isAdminLoggedIn"); // Hapus status login
        navigate("/admin/login", { replace: true });
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // ✅ Fungsi untuk menambahkan atau mengupdate data
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditMode) {
                await updateData();
                setAlertMessage({ type: "success", text: "Data berhasil diperbarui!" });
            } else {
                await addData();
                setAlertMessage({ type: "success", text: "Data berhasil ditambahkan!" });
            }
            fetchData();
            resetForm();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menyimpan data!" });
        }
    };

    const addData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await tambahWisata(formData);
                break;
            case "Manajemen Lawa":
                await tambahLawa(formData);
                break;
            case "Manajemen Baluara":
                await tambahBaluara(formData);
                break;
            default:
                console.warn("Menu tidak valid saat menambah data");
                break; // ✅ Tambahkan default case
        }
    };

    const updateData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await updateWisata(formData.id, formData);
                break;
            case "Manajemen Lawa":
                await updateLawa(formData.id, formData);
                break;
            case "Manajemen Baluara":
                await updateBaluara(formData.id, formData);
                break;
            default:
                console.warn("Menu tidak valid saat mengupdate data");
                break; // ✅ Tambahkan default case
        }
    };

    const handleEdit = (data) => {
        setFormData(data);
        setIsEditMode(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Apakah Anda yakin ingin menghapus data ini?")) return;
        try {
            switch (activeMenu) {
                case "Manajemen Wisata":
                    await deleteWisata(id);
                    break;
                case "Manajemen Lawa":
                    await deleteLawa(id);
                    break;
                case "Manajemen Baluara":
                    await deleteBaluara(id);
                    break;
                default:
                    console.warn("Menu tidak valid saat menghapus data");
                    break; // ✅ Tambahkan default case
            }
            setAlertMessage({ type: "success", text: "Data berhasil dihapus!" });
            fetchData();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menghapus data!" });
        }
    };

    const resetForm = () => {
        setFormData({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
        setIsEditMode(false);
    };

    return (
        <div className="admin-layout">
            {/* Sidebar Menu */}
            <div className="admin-sidebar">
                <h2>DASHBOARD ADMIN</h2>
                <ul>
                    <li onClick={() => setActiveMenu("Manajemen Wisata")}>Manajemen Wisata</li>
                    <li onClick={() => setActiveMenu("Manajemen Lawa")}>Manajemen Lawa</li>
                    <li onClick={() => setActiveMenu("Manajemen Baluara")}>Manajemen Baluara</li>
                </ul>
                <button className="btn-logout" onClick={handleLogout}>Logout</button>
            </div>

            {/* Konten Dinamis */}
            <div className="admin-content">
                <h3>{activeMenu}</h3>

                {/* Alert Message */}
                {alertMessage && (
                    <div className={`alert ${alertMessage.type === "success" ? "alert-success" : "alert-error"}`}>
                        {alertMessage.text}
                    </div>
                )}

                {/* Tabel Data */}
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Nama</th>
                            <th>Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {dataList.length > 0 ? (
                            dataList.map((item, index) => (
                                <tr key={item.id}>
                                    <td>{index + 1}</td>
                                    <td>{item.nama}</td>
                                    <td>
                                        <button className="btn-edit" onClick={() => handleEdit(item)}>Edit</button>
                                        <button className="btn-delete" onClick={() => handleDelete(item.id)}>Hapus</button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="3" className="text-center">Tidak ada data tersedia.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AdminDashboard;
