import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import NavigationBar from "./components/NavigationBar";
import Home from "./pages/Home";
import Detail from "./pages/Detail";
import Destinasi from "./pages/Destinasi";

// Admin Pages
import AdminDashboard from "./pages/admin/AdminDashboard";
import ManajemenWisata from "./pages/admin/ManajemenWisata";
import ManajemenLawa from "./pages/admin/ManajemenLawa";
import ManajemenBaluara from "./pages/admin/ManajemenBaluara";
import EditWisata from "./pages/admin/EditWisata";
import EditLawa from "./pages/admin/EditLawa";
import EditBaluara from "./pages/admin/EditBaluara";
import TambahWisata from "./pages/admin/TambahWisata";
import TambahLawa from "./pages/admin/TambahLawa";
import TambahBaluara from "./pages/admin/TambahBaluara";

const AppContent = () => {
    const [language, setLanguage] = useState("id");
    const [darkMode, setDarkMode] = useState(() => localStorage.getItem("darkMode") === "true");
    const location = useLocation();

    useEffect(() => {
        document.body.classList.toggle("dark-mode", darkMode);
        localStorage.setItem("darkMode", darkMode);
    }, [darkMode]);

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    // Hilangkan navbar di halaman admin
    const isAdminPage = location.pathname.startsWith("/admin");

    return (
        <>
            {!isAdminPage && (
                <NavigationBar
                    language={language}
                    setLanguage={setLanguage}
                    toggleDarkMode={toggleDarkMode}
                    darkMode={darkMode}
                />
            )}
            <Routes>
                {/* Rute User */}
                <Route path="/" element={<Home language={language} darkMode={darkMode} />} />
                <Route path="/detail/:id" element={<Detail language={language} darkMode={darkMode} />} />
                <Route path="/destinasi" element={<Destinasi language={language} darkMode={darkMode} />} />

                {/* Rute Admin */}
                <Route path="/admin/dashboard" element={<AdminDashboard />} />
            </Routes>
        </>
    );
};

const App = () => {
    return (
        <Router>
            <AppContent />
        </Router>
    );
};

export default App;
