import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getWisataById } from "../api";
import { Container, Button } from "react-bootstrap";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const Detail = ({ language, darkMode }) => {
    const { id } = useParams();
    const [wisata, setWisata] = useState(null);
    const [map, setMap] = useState(null);

    useEffect(() => {
        getWisataById(id).then(data => {
            setWisata(data);
            if (data) {
                // Cek apakah peta sudah ada sebelum inisialisasi
                if (!map) {
                    const newMap = L.map("map").setView([data.latitude, data.longitude], 15);
                    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png").addTo(newMap);
                    L.marker([data.latitude, data.longitude]).addTo(newMap)
                        .bindPopup(`<b>${data.nama}</b>`).openPopup();
                    
                    setMap(newMap); // Simpan referensi ke state
                }
            }
        });

        return () => {
            if (map) {
                map.remove(); // Hapus peta saat komponen di-unmount
            }
        };
    }, [id, map]);

    if (!wisata) return <p>{language === "id" ? "Memuat..." : "Loading..."}</p>;

    // Fungsi untuk membuka Google Maps dengan rute ke lokasi wisata
    const handleNavigate = () => {
        window.open(`https://www.google.com/maps/dir/?api=1&destination=${wisata.latitude},${wisata.longitude}`, "_blank");
    };

    return (
        <Container className={`mt-5 pt-5 ${darkMode ? "dark-mode" : ""}`}>
            <h2>{wisata.nama}</h2>
            <img src={`/images/${wisata.gambar}`} alt={wisata.nama} className="img-fluid rounded mb-3" />

            <div className="row">
                <div className="col-md-8">
                    <p>{language === "id" ? wisata.deskripsi_id : wisata.deskripsi_en}</p>
                    <Button variant="primary" onClick={handleNavigate}>
                        {language === "id" ? "Pergi Sekarang" : "Go Now"}
                    </Button>
                </div>
                <div className="col-md-4">
                    {/* Peta Interaktif */}
                    <div id="map" style={{ height: "400px", width: "100%" }}></div>
                </div>
            </div>
        </Container>
    );
};

export default Detail;
