import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getWisataById } from "../api";
import { Container } from "react-bootstrap";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const Detail = ({ language, darkMode }) => {
    const { id } = useParams();
    const [wisata, setWisata] = useState(null);

    useEffect(() => {
        getWisataById(id).then(data => setWisata(data));

        // Inisialisasi Peta
        const map = L.map("map").setView([-5.4856, 122.595], 15);
        L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png").addTo(map);
    }, [id]);

    if (!wisata) return <p>{language === "id" ? "Memuat..." : "Loading..."}</p>;

    return (
        <Container className={`mt-5 pt-5 ${darkMode ? "dark-mode" : ""}`}>
            <h2>{wisata.nama}</h2>
            <img src={`/images/${wisata.gambar}`} alt={wisata.nama} className="img-fluid" />
            <p>{language === "id" ? wisata.deskripsi_id : wisata.deskripsi_en}</p>

            {/* Peta Interaktif */}
            <div id="map" style={{ height: "400px", width: "100%" }}></div>
        </Container>
    );
};

export default Detail;
