import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../css/style.css"; 

const LoginAdmin = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleLogin = (e) => {
        e.preventDefault();

        console.log("Coba login dengan:", username, password);

        if (username === "admin" && password === "admin123") {
            console.log("âœ… Login berhasil! Menyimpan status login...");
            localStorage.setItem("isAdminLoggedIn", "true");

            // Coba redirect pakai timeout untuk menghindari re-render instan
            setTimeout(() => {
                console.log("ðŸ”„ Redirect ke /admin/dashboard...");
                navigate("/admin/dashboard", { replace: true });
            }, 500);
        } else {
            console.log("âŒ Login gagal! Username atau password salah.");
            alert("Username atau password salah!");
        }
    };

    // ðŸ” Debugging: Pastikan admin langsung masuk ke dashboard jika sudah login
    useEffect(() => {
        const isLoggedIn = localStorage.getItem("isAdminLoggedIn") === "true";
        console.log("ðŸ”Ž Cek status login:", isLoggedIn);
        if (isLoggedIn) {
            console.log("ðŸ”„ Sudah login, langsung redirect ke Dashboard...");
            navigate("/admin/dashboard", { replace: true });
        }
    }, [navigate]);

    return (
        <div className="login-container no-darkmode">
            <h2>Admin Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default LoginAdmin;
