import React, { useEffect, useState } from 'react';
import { getWisataById, updateWisata } from '../../api';
import { useParams, useNavigate } from 'react-router-dom';

const EditWisata = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        nama: '',
        deskripsi_id: '',
        deskripsi_en: '',
        gambar: '',
        latitude: '',
        longitude: ''
    });

    useEffect(() => {
        getWisataById(id).then(data => setFormData(data));
    }, [id]);

    const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleUpdate = () => {
        updateWisata(id, formData).then(() => {
            alert('Data berhasil diperbarui');
            navigate('/admin/manajemen-wisata');
        });
    };

    return (
        <div>
            <h2>Edit Wisata</h2>
            <input name="nama" value={formData.nama} onChange={handleChange} placeholder="Nama" />
            <textarea name="deskripsi_id" value={formData.deskripsi_id} onChange={handleChange} placeholder="Deskripsi ID"></textarea>
            <textarea name="deskripsi_en" value={formData.deskripsi_en} onChange={handleChange} placeholder="Deskripsi EN"></textarea>
            <input name="gambar" value={formData.gambar} onChange={handleChange} placeholder="Gambar" />
            <input name="latitude" value={formData.latitude} onChange={handleChange} placeholder="Latitude" />
            <input name="longitude" value={formData.longitude} onChange={handleChange} placeholder="Longitude" />
            <button onClick={handleUpdate}>Simpan</button>
        </div>
    );
};

export default EditWisata;
