import React, { useEffect, useState } from 'react';
import { getWisata } from '../api';
import { Container, Row, Col, Card, Carousel } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Home = ({ language, darkMode }) => {
    const [wisataList, setWisataList] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        getWisata().then(data => setWisataList(data));

        const handleScroll = () => {
            const sections = document.querySelectorAll('.section');
            sections.forEach(section => {
                const sectionTop = section.getBoundingClientRect().top;
                const windowHeight = window.innerHeight;
                if (sectionTop < windowHeight - 100) {
                    section.classList.add('fade-in');
                }
            });
        };

        window.addEventListener('scroll', handleScroll);
        handleScroll();

        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <Container fluid className={`mt-5 pt-5 ${darkMode ? "dark-mode" : ""}`}>
            {/* Gambar Slider */}
            <Carousel className="carousel-container">
                <Carousel.Item>
                    <img className="d-block w-100 carousel-img" src="/images/slide1.jpg" alt="Slide 1" />
                </Carousel.Item>
                <Carousel.Item>
                    <img className="d-block w-100 carousel-img" src="/images/slide2.jpg" alt="Slide 2" />
                </Carousel.Item>
                <Carousel.Item>
                    <img className="d-block w-100 carousel-img" src="/images/slide3.jpg" alt="Slide 3" />
                </Carousel.Item>
            </Carousel>

            {/* Tentang Benteng Keraton Buton */}
            <Container className="mt-4 section">
                <h2 className="text-center mb-3">
                    {language === "id" ? "Tentang Benteng Keraton Buton" : "About Benteng Keraton Buton"}
                </h2>
                <p className="text-center about-text">
                    {language === "id" 
                        ? "Benteng Keraton Buton merupakan benteng terluas di dunia yang terletak di Kota Baubau, Sulawesi Tenggara. Benteng ini menjadi saksi sejarah perkembangan Kesultanan Buton dan menyimpan banyak warisan budaya yang bernilai tinggi." 
                        : "Benteng Keraton Buton is the largest fortress in the world located in Baubau City, Southeast Sulawesi. This fortress stands as a historical witness to the development of the Buton Sultanate and holds many valuable cultural heritages."}
                </p>
            </Container>

            <hr className="section-divider" />

            {/* Daftar Wisata */}
            <Container className="mt-4 section">
                <h2 className="text-center mb-4">
                    {language === "id" ? "Destinasi Wisata" : "Tourist Destinations"}
                </h2>
                <Row>
                    {wisataList.length > 0 ? (
                        wisataList.map(wisata => (
                            <Col key={wisata.id} md={3} xs={6} className="mb-4">
                                <Card className={`shadow-sm ${darkMode ? "dark-card" : ""}`}
                                    onClick={() => navigate(`/detail/${wisata.id}`)}
                                    style={{ cursor: 'pointer' }}>
                                    <Card.Img variant="top" src={`/images/${wisata.gambar}`} />
                                    <Card.Body className="text-center">
                                        <Card.Title>{wisata.nama}</Card.Title>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))
                    ) : (
                        <p className="text-center">
                            {language === "id" ? "Tidak ada destinasi wisata tersedia." : "No tourist destinations available."}
                        </p>
                    )}
                </Row>
            </Container>

            <hr className="section-divider" />
        </Container>
    );
};

export default Home;
