import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../css/style.css";
import {
    getWisata, tambahWisata, updateWisata, deleteWisata,
    getLawa, tambahLawa, updateLawa, deleteLawa,
    getBaluara, tambahBaluara, updateBaluara, deleteBaluara,
    getSliders, tambahSlider, updateSlider, deleteSlider
} from "../../api";

const AdminDashboard = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState("Manajemen Wisata");
    const [dataList, setDataList] = useState([]);
    const [formData, setFormData] = useState({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
    const [sliderData, setSliderData] = useState({ id: null, gambar: "" });
    const [isEditMode, setIsEditMode] = useState(false);
    const [alertMessage, setAlertMessage] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 6;

    useEffect(() => {
        const adminToken = sessionStorage.getItem("adminToken");
        if (!adminToken) {
            navigate("/admin/login");
        }
    }, []);

    useEffect(() => {
        fetchData();
    }, [activeMenu]);

    useEffect(() => {
        if (alertMessage) {
            const timer = setTimeout(() => setAlertMessage(null), 5000);
            return () => clearTimeout(timer);
        }
    }, [alertMessage]);

    const fetchData = async () => {
        let data = [];
        try {
            switch (activeMenu) {
                case "Manajemen Wisata":
                    data = await getWisata();
                    break;
                case "Manajemen Lawa":
                    data = await getLawa();
                    break;
                case "Manajemen Baluara":
                    data = await getBaluara();
                    break;
                case "Manajemen Slider":
                    data = await getSliders();
                    break;
                default:
                    data = [];
            }
            setDataList(data);
        } catch (error) {
            console.error("Gagal mengambil data:", error);
        }
    };

    const handleLogout = () => {
        sessionStorage.removeItem("adminToken");
        navigate("/admin/login");
    };

    const handleInputChange = (e) => {
        if (activeMenu === "Manajemen Slider") {
            setSliderData({ ...sliderData, [e.target.name]: e.target.value });
        } else {
            setFormData({ ...formData, [e.target.name]: e.target.value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditMode) {
                await updateData();
                setAlertMessage({ type: "success", text: "Data berhasil diperbarui!" });
            } else {
                await addData();
                setAlertMessage({ type: "success", text: "Data berhasil ditambahkan!" });
            }
            fetchData();
            resetForm();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menyimpan data!" });
        }
    };

    const addData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await tambahWisata(formData);
                break;
            case "Manajemen Lawa":
                await tambahLawa(formData);
                break;
            case "Manajemen Baluara":
                await tambahBaluara(formData);
                break;
            case "Manajemen Slider":
                await tambahSlider(sliderData);
                break;
        }
    };

    const updateData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await updateWisata(formData.id, formData);
                break;
            case "Manajemen Lawa":
                await updateLawa(formData.id, formData);
                break;
            case "Manajemen Baluara":
                await updateBaluara(formData.id, formData);
                break;
            case "Manajemen Slider":
                await updateSlider(sliderData.id, sliderData);
                break;
        }
    };

    const handleEdit = (data) => {
        if (activeMenu === "Manajemen Slider") {
            setSliderData(data);
        } else {
            setFormData(data);
        }
        setIsEditMode(true);
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Apakah Anda yakin ingin menghapus data ini?")) return;
        try {
            switch (activeMenu) {
                case "Manajemen Wisata":
                    await deleteWisata(id);
                    break;
                case "Manajemen Lawa":
                    await deleteLawa(id);
                    break;
                case "Manajemen Baluara":
                    await deleteBaluara(id);
                    break;
                case "Manajemen Slider":
                    await deleteSlider(id);
                    break;
            }
            setAlertMessage({ type: "success", text: "Data berhasil dihapus!" });
            fetchData();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menghapus data!" });
        }
    };

    const resetForm = () => {
        setFormData({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
        setSliderData({ id: null, gambar: "" });
        setIsEditMode(false);
    };

    return (
        <div className="admin-layout">
            <div className="admin-sidebar">
                <h2>DASHBOARD ADMIN</h2>
                <ul>
                    <li className={activeMenu === "Manajemen Wisata" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Wisata")}>Manajemen Wisata</li>
                    <li className={activeMenu === "Manajemen Lawa" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Lawa")}>Manajemen Lawa</li>
                    <li className={activeMenu === "Manajemen Baluara" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Baluara")}>Manajemen Baluara</li>
                    <li className={activeMenu === "Manajemen Slider" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Slider")}>Manajemen Slider</li>
                </ul>
                <button className="btn-logout" onClick={handleLogout}>Logout</button>
            </div>

            <div className="admin-content">
                <h3>{activeMenu}</h3>
                {alertMessage && <div className={`alert ${alertMessage.type === "success" ? "alert-success" : "alert-error"}`}>{alertMessage.text}</div>}

                <form className="form-admin" onSubmit={handleSubmit}>
                    {activeMenu === "Manajemen Slider" ? (
                        <input type="text" name="gambar" placeholder="Nama File Gambar" value={sliderData.gambar} onChange={handleInputChange} required />
                    ) : (
                        <input type="text" name="nama" placeholder="Nama" value={formData.nama} onChange={handleInputChange} required />
                    )}
                    <button type="submit" className="btn-tambah">{isEditMode ? "Update" : "Tambah"}</button>
                </form>

                <ul className="slider-list">
                    {dataList.map((item) => (
                        <li key={item.id}>
                            <span>{activeMenu === "Manajemen Slider" ? item.gambar : item.nama}</span>
                            <button className="btn-edit" onClick={() => handleEdit(item)}>Edit</button>
                            <button className="btn-delete" onClick={() => handleDelete(item.id)}>Hapus</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default AdminDashboard;
