import React, { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import { getWisataById } from "../api";
import { Container, Button } from "react-bootstrap";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const Detail = ({ language, darkMode }) => {
    const { id } = useParams();
    const [wisata, setWisata] = useState(null);
    const mapRef = useRef(null);

    useEffect(() => {
        getWisataById(id).then(data => {
            if (data && data.latitude && data.longitude) {
                setWisata(data);
            }
        });
    }, [id]);

    useEffect(() => {
        if (wisata && wisata.latitude && wisata.longitude && !mapRef.current) {
            const mapContainer = document.getElementById("map");

            // **Pastikan elemen map ada sebelum Leaflet.js dijalankan**
            if (!mapContainer) {
                console.error("Map container not found");
                return;
            }

            // **Buat peta hanya jika belum ada**
            mapRef.current = L.map("map").setView([wisata.latitude, wisata.longitude], 15);
            L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png").addTo(mapRef.current);
            L.marker([wisata.latitude, wisata.longitude]).addTo(mapRef.current)
                .bindPopup(`<b>${wisata.nama}</b>`).openPopup();
        }

        return () => {
            // **Hapus peta saat komponen dibongkar**
            if (mapRef.current) {
                mapRef.current.remove();
                mapRef.current = null;
            }
        };
    }, [wisata]);

    if (!wisata) return <p>{language === "id" ? "Memuat..." : "Loading..."}</p>;

    const handleNavigate = () => {
        window.open(`https://www.google.com/maps/dir/?api=1&destination=${wisata.latitude},${wisata.longitude}`, "_blank");
    };

    return (
        <Container className={`mt-5 pt-5 ${darkMode ? "dark-mode" : ""}`}>
            <h2>{wisata.nama}</h2>
            <img src={`/images/${wisata.gambar}`} alt={wisata.nama} className="img-fluid rounded mb-3" />

            <div className="row">
                <div className="col-md-8">
                    <p>{wisata.deskripsi}</p>
                    <Button variant="primary" onClick={handleNavigate}>
                        {language === "id" ? "Pergi Sekarang" : "Go Now"}
                    </Button>
                </div>
                <div className="col-md-4">
                    {/* **Tambahkan ID yang benar untuk peta** */}
                    <div id="map" style={{ height: "400px", width: "100%" }}></div>
                </div>
            </div>
        </Container>
    );
};

export default Detail;
