import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getWisata } from '../api';
import { Container, Row, Col } from 'react-bootstrap';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const Detail = () => {
    const { id } = useParams();
    const [wisata, setWisata] = useState(null);

    useEffect(() => {
        getWisata().then(data => {
            const selectedWisata = data.find(item => item.id === parseInt(id));
            setWisata(selectedWisata);
        });
    }, [id]);

    if (!wisata) return <p className="text-center mt-5">Loading...</p>;

    return (
        <Container className="mt-5 pt-5">
            <Row>
                <Col md={8}>
                    <h2>{wisata.nama}</h2>
                    <p>{wisata.deskripsi}</p>
                </Col>
                <Col md={4}>
                    <MapContainer center={[wisata.latitude, wisata.longitude]} zoom={15} style={{ height: "300px", width: "100%" }}>
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        <Marker position={[wisata.latitude, wisata.longitude]}>
                            <Popup>{wisata.nama}</Popup>
                        </Marker>
                    </MapContainer>
                </Col>
            </Row>
        </Container>
    );
};

export default Detail;
