import React, { useEffect, useState } from 'react';
import { getLawa, deleteLawa } from '../../api';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa';
import '../../css/style.css';

const ManajemenLawa = () => {
    const [lawaList, setLawaList] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchLawa();
    }, []);

    const fetchLawa = async () => {
        const data = await getLawa();
        setLawaList(data);
    };

    const handleDelete = async (id) => {
        const confirmDelete = window.confirm("Yakin ingin menghapus Lawa ini?");
        if (confirmDelete) {
            await deleteLawa(id);
            fetchLawa();
        }
    };

    return (
        <div className="admin-container">
            <BackButton />
            <h2>Manajemen Lawa</h2>

            <div className="text-end mb-3">
                <button className="btn-tambah" onClick={() => navigate('/admin/tambah-lawa')}>
                    <FaPlus /> Tambah Lawa
                </button>
            </div>

            <table className="admin-table">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Nama Lawa</th>
                        <th>Aksi</th>
                    </tr>
                </thead>
                <tbody>
                    {lawaList.map((lawa, index) => (
                        <tr key={lawa.id}>
                            <td>{index + 1}</td>
                            <td>{lawa.nama}</td>
                            <td>
                                <button className="btn-edit" onClick={() => navigate(`/admin/edit-lawa/${lawa.id}`)}>
                                    <FaEdit /> Edit
                                </button>
                                <button className="btn-delete" onClick={() => handleDelete(lawa.id)}>
                                    <FaTrash /> Hapus
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManajemenLawa;
