import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../css/style.css";
import {
    getWisata, tambahWisata, updateWisata, deleteWisata,
    getLawa, tambahLawa, updateLawa, deleteLawa,
    getBaluara, tambahBaluara, updateBaluara, deleteBaluara,
    getSliders, tambahSlider, updateSlider, deleteSlider
} from "../../api";

const AdminDashboard = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState("Manajemen Wisata");
    const [dataList, setDataList] = useState([]);
    const [sliderList, setSliderList] = useState([]);
    const [formData, setFormData] = useState({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
    const [sliderForm, setSliderForm] = useState({ id: null, gambar: "" });
    const [isEditMode, setIsEditMode] = useState(false);
    const [alertMessage, setAlertMessage] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 6;

    useEffect(() => {
        const adminToken = localStorage.getItem("adminToken");
        if (!adminToken) {
            navigate("/admin/login");
        }
    }, [navigate]);

    useEffect(() => {
        fetchData();
    }, [activeMenu]);

    useEffect(() => {
        if (alertMessage) {
            const timer = setTimeout(() => setAlertMessage(null), 5000);
            return () => clearTimeout(timer);
        }
    }, [alertMessage]);

    const fetchData = async () => {
        let data = [];
        try {
            switch (activeMenu) {
                case "Manajemen Wisata":
                    data = await getWisata();
                    break;
                case "Manajemen Lawa":
                    data = await getLawa();
                    break;
                case "Manajemen Baluara":
                    data = await getBaluara();
                    break;
                    case "Manajemen Slider": // ðŸ”¹ Ambil data slider
                    data = await getSliders();
                    break;
                default:
                    data = [];
            }
            setDataList(data);
        } catch (error) {
            console.error("Gagal mengambil data:", error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("adminToken");
        navigate("/admin/login");
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSliderInputChange = (e) => {
        setSliderForm({ ...sliderForm, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditMode) {
                await updateData();
                setAlertMessage({ type: "success", text: "Data berhasil diperbarui!" });
            } else {
                await addData();
                setAlertMessage({ type: "success", text: "Data berhasil ditambahkan!" });
            }
            fetchData();
            resetForm();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menyimpan data!" });
        }
    };

    const handleSliderSubmit = async (e) => {
        e.preventDefault();
        try {
            if (sliderForm.id) {
                await updateSlider(sliderForm.id, sliderForm);
                setAlertMessage({ type: "success", text: "Gambar slider berhasil diperbarui!" });
            } else {
                await tambahSlider(sliderForm);
                setAlertMessage({ type: "success", text: "Gambar slider berhasil ditambahkan!" });
            }
            fetchData();
            resetSliderForm();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menyimpan gambar slider!" });
        }
    };

    const addData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await tambahWisata(formData);
                break;
            case "Manajemen Lawa":
                await tambahLawa(formData);
                break;
            case "Manajemen Baluara":
                await tambahBaluara(formData);
                break;
        }
    };

    const updateData = async () => {
        switch (activeMenu) {
            case "Manajemen Wisata":
                await updateWisata(formData.id, formData);
                break;
            case "Manajemen Lawa":
                await updateLawa(formData.id, formData);
                break;
            case "Manajemen Baluara":
                await updateBaluara(formData.id, formData);
                break;
        }
    };

    const resetForm = () => {
        setFormData({ id: null, nama: "", deskripsi_id: "", deskripsi_en: "", longitude: "", latitude: "", gambar: "" });
        setIsEditMode(false);
    };

    const resetSliderForm = () => {
        setSliderForm({ id: null, gambar: "" });
    };

    const handleEditSlider = (slider) => {
        setSliderForm(slider);
    };

    const handleDeleteSlider = async (id) => {
        if (!window.confirm("Apakah Anda yakin ingin menghapus gambar slider ini?")) return;
        try {
            await deleteSlider(id);
            setAlertMessage({ type: "success", text: "Gambar slider berhasil dihapus!" });
            fetchData();
        } catch (error) {
            setAlertMessage({ type: "error", text: "Gagal menghapus gambar slider!" });
        }
    };

    return (
        <div className="admin-layout">
            <div className="admin-sidebar">
                <h2>DASHBOARD ADMIN</h2>
                <ul>
                    <li className={activeMenu === "Manajemen Wisata" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Wisata")}>Manajemen Wisata</li>
                    <li className={activeMenu === "Manajemen Lawa" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Lawa")}>Manajemen Lawa</li>
                    <li className={activeMenu === "Manajemen Baluara" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Baluara")}>Manajemen Baluara</li>
                    <li className={activeMenu === "Manajemen Slider" ? "active" : ""} onClick={() => setActiveMenu("Manajemen Slider")}>Manajemen Slider</li>
                </ul>
                <button className="btn-logout" onClick={handleLogout}>Logout</button>
            </div>

            <div className="admin-content">
                <h3>{activeMenu}</h3>
                {alertMessage && <div className={`alert ${alertMessage.type === "success" ? "alert-success" : "alert-error"}`}>{alertMessage.text}</div>}

                {activeMenu === "Manajemen Slider" ? (
                    <div className="admin-slider">
                        <form className="form-admin" onSubmit={handleSliderSubmit}>
                            <input type="text" name="gambar" placeholder="Nama File Gambar" value={sliderForm.gambar} onChange={handleSliderInputChange} required />
                            <button type="submit" className="btn-tambah">{sliderForm.id ? "Update Gambar" : "Tambah Gambar Slider"}</button>
                        </form>

                        <div className="slider-list">
                            {sliderList.map((slider) => (
                                <div key={slider.id} className="slider-item">
                                    <img src={`/images/${slider.gambar}`} alt="Slider" className="slider-preview" />
                                    <button className="btn-edit" onClick={() => handleEditSlider(slider)}>Edit</button>
                                    <button className="btn-delete" onClick={() => handleDeleteSlider(slider.id)}>Hapus</button>
                                </div>
                            ))}
                        </div>
                    </div>
                ) : (
                    <p>Pilih menu untuk melihat data.</p>
                )}
            </div>
        </div>
    );
};

export default AdminDashboard;
