import React, { useEffect, useState } from 'react';
import { getWisataById, updateWisata } from '../../api';
import { useParams, useNavigate } from 'react-router-dom';

const EditWisata = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [wisata, setWisata] = useState({
        nama: '',
        deskripsi_id: '',
        deskripsi_en: '',
        latitude: '',
        longitude: '',
        gambar: '',
    });

    useEffect(() => {
        getWisataById(id).then(data => setWisata(data));
    }, [id]);

    const handleChange = (e) => {
        setWisata({ ...wisata, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateWisata(id, wisata);
        navigate('/admin/manajemen-wisata');
    };

    return (
        <div className="container mt-5 pt-5">
            <h2>Edit Wisata</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label>Nama Wisata</label>
                    <input type="text" name="nama" className="form-control" value={wisata.nama} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label>Deskripsi (ID)</label>
                    <textarea name="deskripsi_id" className="form-control" value={wisata.deskripsi_id} onChange={handleChange}></textarea>
                </div>
                <div className="mb-3">
                    <label>Deskripsi (EN)</label>
                    <textarea name="deskripsi_en" className="form-control" value={wisata.deskripsi_en} onChange={handleChange}></textarea>
                </div>
                <div className="mb-3">
                    <label>Latitude</label>
                    <input type="text" name="latitude" className="form-control" value={wisata.latitude} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label>Longitude</label>
                    <input type="text" name="longitude" className="form-control" value={wisata.longitude} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label>Gambar</label>
                    <input type="text" name="gambar" className="form-control" value={wisata.gambar} onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-primary">Simpan Perubahan</button>
            </form>
        </div>
    );
};

export default EditWisata;
