import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getLawaById } from '../api';
import { Container, Button } from 'react-bootstrap';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const LawaDetail = ({ language, darkMode }) => {
    const { id } = useParams();
    const [lawa, setLawa] = useState(null);

    useEffect(() => {
        getLawaById(id).then(setLawa);
    }, [id]);

    useEffect(() => {
        if (lawa && lawa.latitude && lawa.longitude) {
            const map = L.map('map').setView([lawa.latitude, lawa.longitude], 16);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map);
            L.marker([lawa.latitude, lawa.longitude]).addTo(map)
                .bindPopup(`<b>${lawa.nama}</b>`).openPopup();
        }
    }, [lawa]);

    if (!lawa) return <p>Loading...</p>;

    return (
        <Container className={`mt-5 pt-5 ${darkMode ? 'dark-mode' : ''}`}>
            <h2>{lawa.nama}</h2>
            <img src={`/images/${lawa.gambar}`} alt={lawa.nama} className="img-fluid rounded mb-3" />
            <p>{language === 'id' ? lawa.deskripsi_id : lawa.deskripsi_en}</p>
            <Button
                variant="primary"
                // onClick={() => window.open(`https://www.google.com/maps/dir/?api=1&destination=${lawa.latitude},${lawa.longitude}`, '_blank')}
            >
                {language === 'id' ? 'Pergi Sekarang' : 'Go Now'}
            </Button>
            <div id="map" style={{ height: "400px", width: "100%", marginTop: "20px" }}></div>
        </Container>
    );
};

export default LawaDetail;
